import React from 'react'

class TextBox extends React.Component{

    state = {
        text: 'Tämä on default teksti!'
    }
    clickHandler(event){
        state.text = 'Tämä on uusi teksti!'
    }
    
    render(){
        return(
            <div className="offset-1">
                <h1>{state.text}</h1>
                <div>
                    <input type='text'/>
                </div>
                <div>
                    <button onClick={clickHandler}>Vaihda Teksti</button>
                </div>
            </div>
        )
    }
}

export default TextBox
