import React from 'react'

class TextBox extends React.Component{
    constructor(){
        super()
        this.state = {
            firstSelect: "Select Country",
            secondSelect: "Select Country"
        }
        this.onFieldChange = this.onFieldChange.bind(this)
    }
    onFieldChange(event){
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    render(){
        return(
            <div class="form-group row">
                <div class="col-xs offset-5 firstSelect">
                    <select class="form-control firstSelect" value={this.state.firstSelect} name="firstSelect" onChange={this.onFieldChange}>
                        <option value="Select Country">Select Country</option>
                        <option value="Finland">Finland</option>
                        <option value="Sweden">Sweden</option>
                        <option value="Russia">Russia</option>
                    </select>
                </div>
                <div class="col-xs">
                    <select class="form-control" value={this.state.secondSelect} name="secondSelect" onChange={this.onFieldChange}>
                        <option value="Select Country">Select Country</option>
                        <option value="Finland">Finland</option>
                        <option value="Sweden">Sweden</option>
                        <option value="Russia">Russia</option>
                    </select>
                </div>
            </div>
        )
    }
}

export default TextBox
