import React from 'react'

class TextBox extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            first: props.first,
            second: props.second,
        }
        this.onFieldChange = this.onFieldChange.bind(this)
        this.clickHandler = this.clickHandler.bind(this)
    }
    onFieldChange(event){
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    clickHandler(event){
        event.preventDefault()
        this.props.countriesSelected(this.state)
    }
    render(){
        return(
            <div>
                <div className="form-group row offset-5">
                    <div className="col-xs">
                        <select className="form-control firstSelect" value={this.state.first} name="first" onChange={this.onFieldChange}>
                            <option value="Select Country">Select Country</option>
                            <option value="Finland">Finland</option>
                            <option value="Sweden">Sweden</option>
                            <option value="Russia">Russia</option>
                        </select>
                    </div>
                    <div className="col-xs">
                        <select className="form-control" value={this.state.second} name="second" onChange={this.onFieldChange}>
                            <option value="Select Country">Select Country</option>
                            <option value="Finland">Finland</option>
                            <option value="Sweden">Sweden</option>
                            <option value="Russia">Russia</option>
                        </select>
                    </div>
                </div>
                <div>
                    <center><button className="btn btn-primary mt-3 mb-3" onClick={this.clickHandler}>Select</button></center>
                </div>
            </div>
        )
    }
}

export default TextBox
