import React from 'react'

class TextBox extends React.Component{
    constructor(props){
        super(props)
        this.onFirstFieldChange = this.onFirstFieldChange.bind(this)
        this.clickHandler = this.clickHandler.bind(this)
        this.onSecondFieldChange = this.onSecondFieldChange.bind(this)
    }
    onFirstFieldChange(event){
        this.props.firstCountry.setState({
            [event.target.name]: event.target.value
        })
    }
    onSecondFieldChange(event){
        this.props.secondCountry.setState({
            [event.target.name]: event.target.value
        })
    }
    clickHandler(event){
        //TO-DO
    }
    render(){
        return(
            <div>
                <div class="form-group row offset-5">
                    <div class="col-xs">
                        <select class="form-control firstSelect" value={this.props.firstCountry} name="firstCountry" onChange={this.onFirstFieldChange}>
                            <option value="Select Country">Select Country</option>
                            <option value="Finland">Finland</option>
                            <option value="Sweden">Sweden</option>
                            <option value="Russia">Russia</option>
                        </select>
                    </div>
                    <div class="col-xs">
                        <select class="form-control" value={this.props.secondCountry} name="secondCountry" onChange={this.onSecondFieldChange}>
                            <option value="Select Country">Select Country</option>
                            <option value="Finland">Finland</option>
                            <option value="Sweden">Sweden</option>
                            <option value="Russia">Russia</option>
                        </select>
                    </div>
                </div>
                <div>
                    <center><button className="btn btn-primary mt-3 mb-3">Select</button></center>
                </div>
            </div>
        )
    }
}

export default TextBox
